mapper.select:
SELECT br.serial serial, br.rUserId rUserId, br.fSerial fSerial, br.rContent rContent, br.isDelete isDelete, to_char(br.rDate, 'YYYY.MM.DD HH24:MI:SS') rDate, br.reportType reportType,
bd.userId userId, bd.bookNo bookNo, bd.category category, bd.title title, bd.content content, to_char(bd.bDate, 'YYYY.MM.DD HH24:MI:SS') bDate, bd.hit hit, bd.isBlocked isBlocked,
bk.title_kor title_kor, bk.title_eng title_eng, bk.author author, bk.country country, bk.coverImg coverImg, to_char(bk.pDate, 'YYYY.MM.DD HH24:MI:SS') pDate FROM DBOARD_REPORTLIST br JOIN DBOARD bd ON br.fSerial = bd.serial JOIN DBOOK bk ON bd.bookNo = bk.bookNo WHERE br.serial = #{serial}

mapper.update:
UPDATE DBOOK SET
		isPermitted = 0
	WHERE bookNo = #{bookNo}

mapper.select:
SELECT bd.serial serial, bd.userId userId, bd.bookNo bookNo, bd.category category, bd.title title, bd.content content, to_char(bd.bDate, 'YYYY.MM.DD HH24:MI:SS') bDate, bd.hit hit, bd.isBlocked isBlocked,
bk.title_kor title_kor, bk.title_eng title_eng, bk.author author, bk.country country, bk.coverImg coverImg, to_char(bk.pDate, 'YYYY.MM.DD HH24:MI:SS') pDate,
(SELECT COUNT(*) FROM DBOARD_COMMENT cmt JOIN dboard bd2 ON cmt.fSerial = bd2.serial WHERE bd2.serial = bd.serial) commentCnt, (SELECT COUNT(*) FROM dboard_thumbUp bdt JOIN dboard bd3 ON bdt.pSerial = bd3.serial WHERE bd3.serial = bd.serial) thumbUpCnt FROM DBOARD bd JOIN DBOOK bk ON bd.bookNo = bk.bookNo WHERE bd.serial = #{_parameter}

SELECT userId, mDate
	FROM DMEMBER
	WHERE userId = #{_parameter}
